mapper.select:
  	SELECT p.* from pay_payment p
	join pay_payment_order o on p.payment_id=o.payment_id
	where o.order_id=#{orderId}

  	SELECT p.* from pay_payment p
	join pay_payment_order o on p.payment_id=o.payment_id


			and o.payment_id=#{paymentId}


			and o.order_id=#{orderId}

 	SELECT p.* from pay_payment p
	join pay_ref_merge_payment r on p.payment_id=r.payment_id
	where r.merge_payment_id=#{mergePaymentId}
